(1, 1, 0, NULL, 2, '<p>How are you?</p>', '', '2013-06-05 23:49:04', 84, '<?xml version="1.0" encoding="UTF-8"?>\n<answers><answer id="51afce705ded33.33792889">Ok</answer><answer correct="true" id="51afce705df0b7.53208397">Fine</answer><answer id="51afce705df245.24660787">Bad</answer><answer id="51afce705df359.32422272">I don''t know</answer></answers>\n', 0, 10),
