INSERT INTO `ljpzg_glossary` (`id`, `word`, `desc`, `ordering`, `state`, `checked_out`, `checked_out_time`, `created_by`) VALUES
(1, 'Компрессор', 'Компрессор (от лат. compressio — сжатие) — устройство промышленного применения для сжатия и подачи воздуха и других газов под давлением.', 1, 1, 0, '0000-00-00 00:00:00', 84),
(2, 'Теплообменник', 'Теплообме́нник— устройство, в котором осуществляется теплообмен между двумя теплоносителями, имеющими различные температуры. По принципу действия теплообменники подразделяются на рекуператоры и регенераторы. В рекуператорах движущиеся теплоносители разделены стенкой. К этому типу относится большинство теплообменников различных конструкций', 2, 1, 0, '0000-00-00 00:00:00', 84),
(3, 'Реактор', 'Реактор - устройство, в котором осуществляется химическая реакция', 3, 1, 0, '0000-00-00 00:00:00', 84),
(4, 'Колонна', 'Колонна — любая жёсткая вертикальная конструкция ', 4, 1, 0, '0000-00-00 00:00:00', 84),
(5, 'Вакуумная колонна', 'Вакуумная колонна (ВК) представляет собой сложную ректификационную колонну, работающую по принципу создания парового орошения за счет однократного испарения сырья.', 5, 1, 0, '0000-00-00 00:00:00', 84),
(6, 'Трубчатая цилиндрическая печь', 'Изобретение относится к нефтеперерабатывающей промышленности, к конвекционным трубчатым печам для нагрева углеводородного сырья за счет сжигания топлива. ', 6, 1, 0, '0000-00-00 00:00:00', 84),
(7, 'Дымовая труба', 'Дымовая труба или дымоход — труба для отвода дымовых газов в атмосферу. Обычно вертикальная труба, но может содержать отдельные горизонтальные или наклонные участки.', 7, 1, 0, '0000-00-00 00:00:00', 84),
(8, 'Перегретый пар', 'Перегретый пар -  пар, нагретый до температуры, превышающей температуру кипения при данном давлении. Перегретый пар используется в циклах различных тепловых машин с целью повышения их КПД. Получение перегретого пара происходит в специальных устройствах — пароперегревателях.', 8, 1, 0, '0000-00-00 00:00:00', 84),
(9, 'Пароэжекторные вакуум-насосы', 'Пароэжекторные вакуум-насосы применяются совместно с конденсаторами. Пароэжекторные вакуум-насосы применяют в ос-довном на бромисто-литиевых абсорбционных холодильных установках для поддержания глубокого вакуума в системе.', 9, 1, 0, '0000-00-00 00:00:00', 84),
(10, 'Буровая вышка', 'Буровая вышка — сооружение, являющееся частью буровой установки, часть бурового оборудования.', 10, 1, 0, '0000-00-00 00:00:00', 84),
(11, 'Катализатор ', 'Катализатор — химическое вещество, ускоряющее реакцию, но не входящее в состав продуктов реакции', 11, 1, 0, '0000-00-00 00:00:00', 84),
(12, 'Котел-утилизатор', 'Котел-утилизатор — котёл, использующий теплоту отходящих газов дизелей или газотурбинных установок, сушильных барабанов, вращающихся и туннельных печей.', 12, 1, 0, '0000-00-00 00:00:00', 84),
(13, 'Аппараты воздушного охлаждения', 'Аппараты воздушного охлаждения (АВО) относятся к теплообменному оборудованию и предназначаются для охлаждения газов и жидкостей и конденсирования паровых и парожидкостных сред в технологических процессах химической, нефтехимической, нефтеперерабатывающей, нефтяной и других отраслей промышленности с давлением среды от 0,6 до 16 МПа, или под вакуумом с остаточным давлением не ниже 665 Па и температурой не выше 400 °С.', 13, 1, 0, '0000-00-00 00:00:00', 84),
(14, 'Железнодорожный транспорт', 'Железнодорожный транспорт - вид наземного транспорта, перевозка грузов и пассажиров на котором осуществляется по рельсовым путям.', 14, 1, 0, '0000-00-00 00:00:00', 84),
(15, 'Автобитумовозы', 'Автобитумовозы предназначены для перевозки и выдачи вязких дорожных битумов в жидком состоянии с температурой 160-200 °С от мест его производства до базы потребителя, а также к месту ведения дорожных работ.', 15, 1, 0, '0000-00-00 00:00:00', 84),
(16, 'Мостовой кран ', 'Краном мостового типа - называется кран с грузозахватным устройством, подвешенным к грузовой тележке или тали, которые перемещаются по подвижной стальной конструкции (мосту). Различают краны общего назначения (с крюком), а также специальные (с грейфером, магнитом, захватами для контейнеров) и металлургические.', 16, 1, 0, '0000-00-00 00:00:00', 84),
(17, 'Печь (вертикальные, цилиндрические и т.п.)', 'Цилиндрическая печь отличается вертикальным расположением труб по периферии. В этой печи тепловая нагрузка экрана распределена равномерно в радиальном направлении, но по длине труб она меняется, уменьшаясь снизу вверх при нижнем расположении форсунок. Для усиления теплоотдачи к верхней части труб на выходе из камеры радиации расположен радиирующий конус. Камера конвекции в этих печах обычно отсутствует и заменяется воздухоподогревателем, так как температура газов, покидающих камеру радиации, в этих печах обычно низкая.', 17, 1, 0, '0000-00-00 00:00:00', 84),
(18, 'Плунжерный насос', 'Поршневой насос (плунжерный насос) — один из видов объёмных гидромашин, в котором вытеснителями являются один или несколько поршней (плунжеров), совершающих возвратно-поступательное движение.', 18, 1, 0, '0000-00-00 00:00:00', 84),
(19, 'Поршневой насос', 'Поршневой насос (плунжерный насос) — один из видов объёмных гидромашин, в котором вытеснителями являются один или несколько поршней (плунжеров), совершающих возвратно-поступательное движение.', 19, 1, 0, '0000-00-00 00:00:00', 84),
(20, 'Газгольдер', 'Газгольдер (англ. gas-holder) — большой резервуар для хранения природного, биогаза, или сжиженного нефтяного газа. Различают газгольдеры переменного и постоянного объёма.', 20, 1, 0, '0000-00-00 00:00:00', 84),
(21, 'Ёмкость', 'Ёмкость — сосуд, контейнер или резервуар для хранения или транспортирования жидкостей, газов или сыпучих тел.', 21, 1, 0, '0000-00-00 00:00:00', 84),
(22, 'Сепаратор', 'Сепаратор — аппарат, производящий разделение продукта на фракции с разными характеристиками (например, одну жидкость отделить от другой — моторное масло и вода, или отделить взвеси от жидкости — вино и осадок, и т. д.)', 22, 1, 0, '0000-00-00 00:00:00', 84),
(23, 'Конденсатор-холодильник', 'Конденсаторы-холодильники представляют собой трубные змеевики, погруженные в металлический ( или железобетонный) прямоугольный ящик, в который непрерывно поступает охлаждающая вода. В змеевики подается конденсируемая или охлаждаемая среда. Для конденсаторов змеевики подключают несколькими параллельными потоками посредством коллектора. Для холодильников все трубы собирают в один или два змеевика в зависимости от количества охлаждаемой жидкости.', 23, 1, 0, '0000-00-00 00:00:00', 84),
(24, 'Ректификация', 'Ректификация (от лат. rectus — прямой и facio — делаю) — это процесс разделения бинарных или многокомпонентных смесей за счет противоточного массо- и теплообмена между паром и жидкостью. Ректификацию можно проводить периодически или непрерывно. Ректификацию проводят в башенных колонных аппаратах, снабженных контактными устройствами (тарелками или насадкой) ректификационных колоннах.', 24, 1, 0, '0000-00-00 00:00:00', 84),
(25, 'Метод Клауса', 'Процесс Клауса — процесс каталитической окислительной конверсии сероводорода. Источник сероводорода - природные и промышленные. Природными источниками являются месторождения нефти и газа, вулканическая деятельность, разложение биомассы и т.д.\r\nПромышленнные источники - переработка нефти и газа (процессы гидроочистки и гидрокрекинга), металлургия и т.д. Сероводород, получаемый с гидрогенизационных процессов переработки сернистых и высокосернистых нефтей, газоконденсатов и установок аминной очистки нефтяных и природных газов, обычно используют на НПЗ для производства элементной серы, иногда для производства серной кислоты.', 25, 1, 0, '0000-00-00 00:00:00', 84),
(26, 'Десорбция', 'Десорбция (от де... и лат. sorbeo — поглощаю), удаление адсорбированного вещества с поверхности адсорбента. Десорбция обратна адсорбции и происходит при уменьшении концентрации адсорбируемого вещества в среде, окружающей адсорбент, а также при повышении температуры. Десорбцию применяют для извлечения из адсорбентов поглощённых ими газов, паров или растворённых веществ, а также для регенерации адсорбента. Практически при десорбции через слой адсорбента продувают горячий водяной пар, воздух или инертные газы, увлекающие ранее поглощённое вещество, или промывают слой адсорбента различными реагентами, которые растворяют адсорбированное вещество. Адсорбент после десорбции обычно сушат и охлаждают. Скорость десорбции зависит от температуры, природы и скорости потока десорбирующего газа или растворителя, а также от особенностей структуры адсорбента. Десорбция — один из обязательных циклов при адсорбции в аппаратах периодического действия. Десорбция в адсорберах с подвижным адсорбционным слоем протекает непрерывно.', 26, 1, 0, '0000-00-00 00:00:00', 84),
(27, 'Абсорбция', 'Абсорбция — процесс избирательного поглощения компонентов газовой смеси жидким поглотителем (абсорбентом). Процесс абсорбции происходит в том случае, когда парциальное давление извлекаемого компонента в газовой смеси выше, чем в жидком абсорбенте, вступающем в контакт с этим газом, т.е. для протекания абсорбции необходимо, чтобы газ и абсорбент не находились в состоянии равновесия. Различие в парциальном давлении извлекаемого компонента в газе и жидкости является той движущей силой, под действием которой происходит поглощение (абсорбция) данного компонента жидкой фазой из газовой фазы. Чем больше эта движущая сила, тем интенсивнее переходит этот компонент из газовой фазы в жидкую', 27, 1, 0, '0000-00-00 00:00:00', 84),
(28, 'Коксование замедленное', 'Замедленное (полунепрерывное) коксование, наиб. распространено в мировой практике. Сырье, предварительно нагретое в трубчатых печах до 350-380 °С, непрерывно контактирует в ниж. части ректификац. колонны, к-рая работает при атм. давлении, с парами, подаваемыми из реакц. аппаратов.\r\nКоксование — процесс переработки твёрдого топлива нагреванием без доступа кислорода. При разложении топлива образуются твёрдый продукт — кокс и летучие продукты.', 28, 1, 0, '0000-00-00 00:00:00', 84),
(29, 'Стабилизация бензина (дебутанизация)', 'Стабилизация бензиновой фракции, получаемой при первичной перегонке нефти, необходима как при применении ее как компонента товарного продукта, так и при использовании в качестве сырья вторичной перегонки с получением узких фракций.Стабилизация бензина осуществляется либо на комбинированной установке по перегонке нефти, либо блок стабилизации имеется в составе установки вторичной перегонки бензина.', 29, 1, 0, '0000-00-00 00:00:00', 84),
(30, 'Гидроочистка', 'Гидроочистка — процесс химического превращения веществ под воздействием водорода при высоком давлении и температуре. Гидроочистка нефтяных фракций направлена на снижение содержания сернистых соединений в товарных нефтепродуктах. Побочно происходит насыщение непредельных углеводородов, снижение содержания смол, кислородсодержащих соединений, а также гидрокрекинг молекул углеводородов. Наиболее распространённый процесс нефтепереработки. Гидроочистке подвергаются следующие фракции нефти:\r\n\r\nБензиновые фракции (прямогонные и каталитического крекинга);\r\nКеросиновые фракции;\r\nДизельное топливо;\r\nВакуумный газойль;\r\nФракции масел.\r\n', 30, 1, 0, '0000-00-00 00:00:00', 84),
(31, 'Окисление', 'Окисление - химические взаимодействия или процессы, при которых происходит соединение тел с кислородом.', 31, 1, 0, '0000-00-00 00:00:00', 84),
(32, 'Тяжелый газойль', 'Тяжелый газойль - маловязкий компонент котельного топлива (до 20%).\r\nГазойль, смесь углеводородов разл. строения, преим. С12—С35, и примесей (гл. обр. серо-, азот- и кислородсодержащих) с пределами выкипания 200-500 °С и мол. м. 150-500. Производят дистилляцией нефти или продуктов ее переработки. При прямой перегонке нефти в условиях атм. давления получают атм. газойль (фракция с пределами выкипания 270-360 °С), при давл. 10-15 кПа-вакуумный газойль (350-500 °С). В зависимости от природы нефти углеводородный состав газойля изменяется в широких пределах (содержание парафино-нафтеновых углеводородов 20-70%, остальное - ароматич. углеводороды и примеси, содержащие гетероатомы). Атм. газойль-компонент дизельного топлива (до 20%) или сырье для каталитич. крекинга.', 32, 1, 0, '0000-00-00 00:00:00', 84),
(33, 'Легкий газойль', 'Легкий газойль подвергают сначала облагораживанию, что позволяет значительно снизить содержание примесей гетероатомных соед. (напр., S с 0,7-1,3 до 0,2-0,5%), и используют как добавку к дизельному топливу (до 30%).', 33, 1, 0, '0000-00-00 00:00:00', 84),
(34, 'Бензин', 'Бензин (франц. benzine), смесь углеводородов различного строения, бесцветная жидкость с пределами кипения 33—205°С. Температура замерзания бензина ниже —60°С, температура вспышки ниже 0°С, плотность 700—780 кг/м3 (0,70—0,78 г/см3).', 34, 1, 0, '0000-00-00 00:00:00', 84),
(35, 'Вакуумный газойль', 'Вакуумный газойль - Широкая углеводородная фракция, полученная при вакуумной дистилляции мазута. Применяется для производства базовых масел и твердых парафинов, в качестве сырья установок пиролиза, гидрокрекинга и каталитического крекинга.', 35, 1, 0, '0000-00-00 00:00:00', 84),
(36, 'Гудрон', 'Гудрон (фр. goudron) — остаток, образующийся в результате отгонки из нефти при атмосферном давлении и под вакуумом фракций, выкипающих до 450—600 °C (в зависимости от природы нефти).\r\nВыход гудрона — от 10 до 45 % от массы нефти. Гудрон — вязкая жидкость или твердый асфальтоподобный продукт черного цвета с блестящим изломом. Содержит парафиновые, нафтеновые и ароматические углеводороды (45-95 %), асфальтены (3-17 %), а также нефтяные смолы (2-38 %), адсорбируемые силикагелем из деасфальтизированного продукта.', 36, 1, 0, '0000-00-00 00:00:00', 84),
(37, 'Мазут', 'Мазут (также костр. мазута; возможно, от арабского "мазхулат" — "отбросы") — жидкий продукт тёмно-коричневого цвета, остаток после выделения из нефти или продуктов её вторичной переработки бензиновых, керосиновых и газойлевых фракций, выкипающих до 350—360°С.', 37, 1, 0, '0000-00-00 00:00:00', 84),
(38, 'Битумы:  дорожные, строительные и кровельные нефтебитумы', 'Битумы (от лат. bitumen — горная смола, нефть) — твёрдые или смолоподобные продукты, представляющие собой смесь углеводородов и их азотистых, кислородистых, сернистых и металлосодержащих производных. Битумы не растворимы в воде, полностью или частично растворимы в бензоле, хлороформе, сероуглероде и др. органических растворителях; плотностью 0,95—1,50 г/см³.', 38, 1, 0, '0000-00-00 00:00:00', 84),
(39, 'Кокс нефтяной', 'Кокс нефтяной (углерод нефтяного происхождения) — твердый остаток вторичной переработки нефти или нефтепродуктов. Используется для изготовления электродов и коррозионноустойчивой аппаратуры, восстановитель при получении ферросплавов и др.', 39, 1, 0, '0000-00-00 00:00:00', 84),
(40, 'Смолы', 'Смолa — собирательное название аморфных веществ, относительно твёрдых при нормальных условиях и размягчающихся или теряющих форму при нагревании. Среди них как сложные по химическому составу органические вещества, например, природные смолы — вещества, выделяемые растениями при нормальном физиологическом обмене, так и химически относительно простые соединения (смола ПВХ).', 40, 1, 0, '0000-00-00 00:00:00', 84),
(41, 'Топливо печное', 'Топливо — вещество или несколько веществ, из которых с помощью определённой реакции может быть получена тепловая энергия.\r\nПечное бытовое топливо предназначено для сжигания в отопительных установках небольшой мощности, расположенных непосредственно в жилых помещениях, а также в теплогенераторах средней мощности, используемых в сельском хозяйстве для приготовления кормов, сушки зерна, фруктов, консервирования и других целей.', 41, 1, 0, '0000-00-00 00:00:00', 84),
(42, 'Элементарная сера', 'Элементарная сера это побочный продукт, получаемый из нефти путем удаления из нее серных соединений и ее переработки на технологической установке Klaus в элементарную серу. Этот продукт получается из газообразной фазы конденсацией серы в жидкую форму, после чего из жидкой серы охлаждением ее образуется кристаллическая сера высокой степени чистоты. Минимальное значение чистоты не менее 99,95 весовых процентов.', 42, 1, 0, '0000-00-00 00:00:00', 84),
(43, 'Окись серы', 'Окись серы - SO2. В нормальных условиях представляет собой бесцветный газ с характерным резким запахом (запах загорающейся спички). Под давлением сжижается при комнатной температуре. Растворяется в воде с образованием нестойкой серни́стой кислоты; растворимость 11,5 г/100 г воды при 20 °C, снижается с ростом температуры. Растворяется также в этаноле, се́рной кислоте. SO2 — один из основных компонентов вулканических газов.', 43, 1, 0, '0000-00-00 00:00:00', 84),
(44, 'Сероводород', 'Сероводород (сернистый водород, сульфид водорода, дигидросульфид) — бесцветный газ с запахом протухших яиц и сладковатым вкусом. Химическая формула — H2S. Плохо растворим в воде, хорошо — в этаноле. Ядовит. При больших концентрациях разъедает многие металлы. Концентрационные пределы воспламенения с воздухом составляют 4,5 — 45 % сероводорода.\r\nВ природе встречается очень редко в виде смешанных веществ нефти и газа. Также иногда содержится в воде. Входит в состав вулканических газов.', 44, 1, 0, '0000-00-00 00:00:00', 84),
(45, 'Моноэтаноламин', 'Моноэтаноламин – горючая жидкость, его водные растворы – трудно горючи. По степени воздействия на организм относится к веществам II класса опасности. Обладает щелочными свойствами, поэтому при попадании на открытые участки кожи, есть риск ожога. Средствами тушения являются распыленная вода, воздушно-механическая пена, порошок.Моноэтаноламин (МЭА) получают взаимодействием окиси этилена и аммиака.', 45, 1, 0, '0000-00-00 00:00:00', 84),
(46, 'Асфальтены', 'Асфальте́ны — наиболее высокомолекулярные компоненты нефти. Твёрдые хрупкие вещества чёрного или бурого цвета; размягчаются в инертной атмосфере при 200—300 °С с переходом в пластичное состояние; плотность около 1,1 г/см3; среднечисленная молекулярная масса 1000—5000, индекс полидисперсности 1,2—3,5. Растворим в бензоле, CS2,CHCl3, CCl4, не растворим в парафиновых углеводородах, спирте, эфире, ацетоне. Содержание асфальтенов в нефтях колеблется от 1 до 20%. Элементный состав (%): С (80—86), Н (7—9), О (2—10), S (0,5—9), N (до 2); в микроколичествах присутствуют V и Ni (суммарное содержание 0,01—0,2%), Fe, Ca, Mg, Cu и др. металлы, входящие в состав металлокомплексных соединений, например, металлопорфиринов.', 46, 1, 0, '0000-00-00 00:00:00', 84),
(47, 'Карбоиды', 'компоненты, нерастворимые в сероуглероде и в других органических растворителях. Образуют группу высокоассоциированных веществ, которые являются главной составной частью коксов, сажи, фюзена, антрацитов, керитЬв и антраксолитов. Для них характерны очень высокая степень уплотненности молекулярной структуры и вследствие этого большое содержание углерода, полная нерастворимость и нелетучесть, черный цвет и высокая непрозрачность', 47, 1, 0, '0000-00-00 00:00:00', 84),
(48, 'Оборотная вода', 'ОБОРОТНАЯ ВОДА — техническая вода, многократно используемая в технологических операциях обогащения полезных ископаемых, припылеулавливании и охлаждении в теплообменных аппаратах на обогатительной, окомковательной и агломерационной фабриках, а также при гидромеханизации горных работ. Обогатительную воду получают из технологических стоков (всего предприятия или отдельных технологических операций) путём их осветления и химической очистки (кондиционирования). Степень осветления зависит от влияния содержания твёрдой взвеси в обогатительной воде на те операции и процессы, где она применяется. Химическая очистка осуществляется только в крайне необходимых случаях, например при флотации.', 48, 1, 0, '0000-00-00 00:00:00', 84),
(49, 'Фено́л ', '(гидроксибензол, устаревшее карболовая кислота) C6H5OH — простейший представитель класса фенолов. Бесцветные игольчатыекристаллы, розовеющие на воздухе из-за окисления, приводящего к образованию окрашенных веществ. Обладают специфическим запахом (таким, как запах гуаши, т. к. в состав гуаши входит фенол). Умеренно растворим в воде (6 г на 100 г воды), в растворах щелоче́й, в спирте, в бензоле, в ацетоне. 5 % раствор в воде — антисептик, широко применяемый в медицине.', 49, 1, 0, '0000-00-00 00:00:00', 84),
(50, 'Пар', 'газообразное состояние вещества в условиях, когда газовая фаза может находиться в равновесии с жидкой или твёрдой фазами того же вещества. Процесс возникновения пара из жидкой (твёрдой) фазы называется «парообразованием». Обратный процесс называется конденсация. При низких давлениях и высоких температурах свойства пара приближаются к свойствам идеального газа. В разговорной речи под словом «пар» почти всегда понимают водяной пар. Пары́ прочих веществ оговариваются в явном виде. Не следует путать оптически однородный и гомогенный пар с туманом — гетерогенной системой, сильно рассеивающей свет.', 50, 1, 0, '0000-00-00 00:00:00', 84),
(51, 'Плотность', 'скалярная физическая величина, определяемая как отношение массы тела к занимаемому этим телом объёму или площади (поверхностная плотность)', 51, 1, 0, '0000-00-00 00:00:00', 84),
(52, 'Вязкость', '(вну́треннее тре́ние) — одно из явлений переноса, свойство текучих тел (жидкостей и газов) оказывать сопротивление перемещению одной их части относительно другой. В результате происходит рассеяние в виде тепла работы, затрачиваемой на это перемещение.', 52, 1, 0, '0000-00-00 00:00:00', 84),
(53, 'Метод кольца и шара', 'КиШ (метод «Кольца и Шара») — метод определения температуры размягчения нефтяных битумов по кольцу и шару. Сущность метода заключается в определении температуры, при которой битум, находящийся в кольце заданных размеров, в условиях испытания размягчается и, перемещаясь под действием стального шарика, касается нижней пластинки - в этот момент фиксируется температура размягчения. Метод является важным для определения физико-химических свойств марок битумов.', 53, 1, 0, '0000-00-00 00:00:00', 84),
(54, 'Пенетрация', '(рабочее смачивание) (лат. penetratio — проникать) — мера проникновения конусного тела в вязкую среду, употребляемая для характеристики консистенции (густоты) веществ. Пенетрационные методы измерения особенно полезны в случае веществ, которые меняют свои реологические свойства при перемешивании.', 54, 1, 0, '0000-00-00 00:00:00', 84),
(55, 'Коксуемость ', 'способность горючих ископаемых образовывать при нагреве остаток в виде пластичного слоя после испарения летучих фракций', 55, 1, 0, '0000-00-00 00:00:00', 84),
(56, 'Температура самовоспламенения', 'наименьшая температура горючего вещества, при нагреве до которой происходит резкое увеличение скорости экзотермических объёмных реакций, приводящее к возникновению пламенного горения и/или взрыва. Эта температура требуется для достижения энергии активации реакции горения.', 56, 1, 0, '0000-00-00 00:00:00', 84),
(57, 'Температура вспышки', 'наименьшая температура летучего конденсированного вещества, при которой пары над поверхностью вещества способны вспыхивать в воздухе под воздействием источника зажигания, однако устойчивое горение после удаления источника зажигания не возникает. Вспышка — быстрое сгорание смеси паров летучего вещества с воздухом, сопровождающееся кратковременным видимым свечением. Температуру вспышки следует отличать как от температуры воспламенения, при которой горючее вещество способно самостоятельно гореть после прекращения действия источника зажигания, так и от температуры самовоспламенения, при которой для инициирования горения или взрыва не требуется внешний источник зажигания.', 57, 1, 0, '0000-00-00 00:00:00', 84),
(58, 'Водородный показатель pH', 'Водоро́дный показа́тель, pH (лат. pondus Hydrogenii - сила водорода, произносится «пэ аш», английское произношение — piː''eɪtʃ) — мера активности (в очень разбавленных растворах она эквивалентна концентрации) ионов водорода в растворе, и количественно выражающая его кислотность, вычисляется как отрицательный (взятый с обратным знаком) десятичный логарифм активности водородных ионов, выраженной в молях на один литр', 58, 1, 0, '0000-00-00 00:00:00', 84),
(59, 'Турбулизатор', 'устройство на обтекаемой поверхности ЛА или его модели для внесения в обтекающий поток возмущений с целью его дестабилизации и смещения вверх по потоку точки перехода ламинарного течения в турбулентное', 59, 1, 0, '0000-00-00 00:00:00', 84);
